package org.springframework.services;import org.springframework.Connection;import org.springframework.User;import org.springframework.context.support.ClassPathXmlApplicationContext;import java.util.Scanner;public class ChangePIN implements BankService{    @Override    public void makeService(int num) {        ClassPathXmlApplicationContext context = new ClassPathXmlApplicationContext("applicationContext.xml");        Connection connection = context.getBean("dBConnection", Connection.class);        Scanner sc = new Scanner(System.in);        System.out.println("please add the pass: ");        int a = sc.nextInt();        connection.changePin(a, num);        System.out.println("New pass" + a);    }}