package org.springframework.services;import org.springframework.User;import java.util.Scanner;public class ChangePIN implements BankService{    @Override    public void makeService(User user) {        System.out.println("change pin, length = 4 (numbers)");        Scanner sc = new Scanner(System.in);        System.out.println("Please add the new PIN: ");        int a = sc.nextInt();        int s = a;        int count = 0;        while (a != 0) {            count++;            a /= 10;        }        System.out.println(count);        if(count == 4) {            user.setPass(s);        }        else{            System.out.println("Invalid input");            System.exit(0);        }    }}