package org.springframework.services;import org.springframework.Connection;import org.springframework.User;import org.springframework.context.support.ClassPathXmlApplicationContext;import org.springframework.services.BankService;import java.util.Scanner;public class TopUp implements BankService {    @Override    public void makeService(int num) {        ClassPathXmlApplicationContext context = new ClassPathXmlApplicationContext("applicationContext.xml");        Connection connection = context.getBean("dBConnection", Connection.class);        Scanner sc = new Scanner(System.in);        System.out.println("please add the number: ");        int a = sc.nextInt();        connection.changeBalance(a, num);    }}